import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { FaGithub, FaLinkedin, FaTwitter, FaEnvelope, FaHeart } from 'react-icons/fa';
import ProjectCard from '../components/ProjectCard';
import axios from 'axios';

const Home = () => {
  const [featuredProjects, setFeaturedProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFeaturedProjects = async () => {
      try {
        const response = await axios.get('/api/projects/featured');
        setFeaturedProjects(response.data);
      } catch (error) {
        console.error('Error fetching featured projects:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchFeaturedProjects();
  }, []);

  // Custom cursor functionality
  useEffect(() => {
    const nameElement = document.querySelector('.custom-cursor');
    if (nameElement) {
      const handleMouseEnter = () => {
        document.body.style.cursor = `url('/cursor.png'), auto`;
      };
      
      const handleMouseLeave = () => {
        document.body.style.cursor = 'auto';
      };

      nameElement.addEventListener('mouseenter', handleMouseEnter);
      nameElement.addEventListener('mouseleave', handleMouseLeave);

      return () => {
        nameElement.removeEventListener('mouseenter', handleMouseEnter);
        nameElement.removeEventListener('mouseleave', handleMouseLeave);
      };
    }
  }, []);

  const skills = [
    'React', 'Node.js', 'MongoDB', 'JavaScript', 'TypeScript', 'Python',
    'Tailwind CSS', 'Express.js', 'Git', 'Docker', 'AWS', 'PostgreSQL'
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="section-padding bg-gradient-to-br from-primary-50 to-blue-100">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-center"
          >
            <h1 className="text-4xl md:text-6xl font-bold text-gray-900 mb-6 font-playwrite">
              Hi, I'm   
                  <span className="relative group inline-block custom-cursor text-5xl md:text-6xl mb-6 font-bold bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-pink-500 font-playwrite">
                    Prince Sharma
                  
                </span>
              </h1>
            <p className="text-xl md:text-2xl text-gray-600 mb-8 max-w-3xl mx-auto font-playwrite">
              Full Stack Developer passionate about creating beautiful and functional web applications
            </p>
            <div className="flex justify-center space-x-4 mb-8">
              <a
                href="https://github.com/yourusername"
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-600 hover:text-primary-600 transition-colors duration-200"
              >
                <FaGithub size={24} />
              </a>
              <a
                href="https://linkedin.com/in/yourusername"
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-600 hover:text-primary-600 transition-colors duration-200"
              >
                <FaLinkedin size={24} />
              </a>
              <a
                href="https://twitter.com/yourusername"
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-600 hover:text-primary-600 transition-colors duration-200"
              >
                <FaTwitter size={24} />
              </a>
              <a
                href="mailto:your.email@example.com"
                className="text-gray-600 hover:text-primary-600 transition-colors duration-200"
              >
                <FaEnvelope size={24} />
              </a>
            </div>
            <div className="flex flex-col sm:flex-row gap-4 justify-center font-playwrite">
              <a href="/projects" className="btn-primary">
                View My Work
              </a>
              <a href="/contact" className="btn-secondary">
                Get In Touch <FaHeart className="inline text-red-500 mx-2 my-2" />
              </a>
            </div>
          </motion.div>
        </div>
      </section>

      {/* About Section */}
      <section className="section-padding">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            className="grid md:grid-cols-2 gap-12 items-center"
          >
            <div>
              <h2 className="text-3xl font-bold text-gray-900 mb-6 font-playwrite">About Me</h2>
              <p className="text-lg text-gray-600 mb-6 font-playwrite">
                I'm a passionate full-stack developer with experience in building modern web applications. 
                I love solving complex problems and creating user-friendly interfaces that make a difference.
              </p>
              <p className="text-lg text-gray-600 mb-6 font-playwrite">
                With expertise in React, Node.js, and MongoDB, I specialize in creating scalable and 
                maintainable applications that deliver exceptional user experiences.
              </p>
              <div className="flex flex-wrap gap-2">
                {skills.map((skill, index) => (
                  <span
                    key={index}
                    className="bg-gray-100 text-gray-700 px-3 py-1 rounded-full text-sm font-medium"
                  >
                    {skill}
                  </span>
                ))}
              </div>
            </div>
            <div className="bg-gray-200 rounded-lg h-96 flex items-center justify-center">
                <p className="text-gray-500 font-playwrite">Prince Sharma</p>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Featured Projects Section */}
      <section className="section-padding bg-gray-50">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            className="text-center mb-12"
          >
            <h2 className="text-3xl font-bold text-gray-900 mb-4 font-playwrite">Featured Projects</h2>
            <p className="text-lg text-gray-600 max-w-2xl mx-auto font-playwrite ">
              Here are some of my recent projects that showcase my skills and passion for development.
            </p>
          </motion.div>

          {loading ? (
            <div className="text-center">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
            </div>
          ) : (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {featuredProjects.slice(0, 3).map((project, index) => (
                <motion.div
                  key={project._id}
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  viewport={{ once: true }}
                >
                  <ProjectCard project={project} />
                </motion.div>
              ))}
            </div>
          )}

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.3 }}
            viewport={{ once: true }}
            className="text-center mt-12"
          >
            <a href="/projects" className="btn-primary font-playwrite">
              View All Projects
            </a>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default Home;
