import React, { useRef, useState } from 'react';

const Home = () => {
  const textRef = useRef(null);
  const [position, setPosition] = useState({ x: 0, y: 0 });

  const handleMouseMove = (e) => {
    const rect = textRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left - rect.width / 2;
    const y = e.clientY - rect.top - rect.height / 2;
    setPosition({ x, y });
  };

  const handleMouseLeave = () => {
    setPosition({ x: 0, y: 0 });
  };

  const style = {
    transform: `translate(${position.x * 0.3}px, ${position.y * 0.3}px)`,
    transition: "transform 0.1s ease-out",
    display: "inline-block",
  };

  return (
    <section className="min-h-screen flex items-center justify-center bg-gray-900 text-white p-10">
      <div className="relative w-full max-w-4xl h-96 rounded-xl overflow-hidden shadow-2xl">

        {/* Background Image */}
        <div 
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage: `url('https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80')`
          }}
        ></div>

        {/* Overlay */}
        <div className="absolute inset-0 bg-black bg-opacity-50" />

        {/* Magnetic Text */}
        <div className="absolute inset-0 flex items-center justify-center z-10">
          <span
            ref={textRef}
            onMouseMove={handleMouseMove}
            onMouseLeave={handleMouseLeave}
            style={style}
            className="text-4xl md:text-5xl font-bold cursor-pointer"
          >
            Prince Sharma
          </span>
        </div>
      </div>
    </section>
  );
};

export default Home;
