import React, { useState, useEffect } from 'react';
import './HeroSection.css';

const HeroSection = () => {
  const [activeSection, setActiveSection] = useState(0);

  const sections = [
    { id: 'prince', text: 'Prince', color: '#DEF06C' },
    { id: 'forest', text: 'Forest', color: '#71C1D1' },
    { id: 'canyon', text: 'Canyon', color: '#DD7171' },
    { id: 'ocean', text: 'Ocean', color: '#A871D1' },
    { id: 'mountain', text: 'Mountain', color: '#F4A261' },
    { id: 'desert', text: 'Desert', color: '#E76F51' },
    { id: 'valley', text: 'Valley', color: '#2A9D8F' },
    { id: 'river', text: 'River', color: '#264653' }
  ];

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;
      const sectionIndex = Math.floor(scrollPosition / windowHeight);
      setActiveSection(Math.min(sectionIndex, sections.length - 1));
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [sections.length]);

  return (
    <div className="hero-container">
      {/* Background Image */}
      <div className="hero-background">
        <div className="background-image"></div>
        <div className="background-overlay"></div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {sections.map((section, index) => (
          <section
            key={section.id}
            className={`concept-section ${index === activeSection ? 'active' : ''}`}
            style={{ '--section-color': section.color }}
          >
            <div className="section-content">
              <h1 
                className="animated-text"
                data-text={section.text}
                style={{ '--text-color': section.color }}
              >
                {section.text}
              </h1>
              
              <div className="text-layers">
                <span className="text-layer layer-1">{section.text}</span>
                <span className="text-layer layer-2">{section.text}</span>
                <span className="text-layer layer-3">{section.text}</span>
                <span className="text-layer layer-4">{section.text}</span>
                <span className="text-layer layer-5">{section.text}</span>
              </div>
            </div>
          </section>
        ))}
      </div>

      {/* Scroll Indicator */}
      <div className="scroll-indicator">
        <div className="scroll-dots">
          {sections.map((_, index) => (
            <div
              key={index}
              className={`scroll-dot ${index === activeSection ? 'active' : ''}`}
              onClick={() => {
                window.scrollTo({
                  top: index * window.innerHeight,
                  behavior: 'smooth'
                });
              }}
            ></div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
