import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './AnimatedHamburgerMenu.css';

const AnimatedHamburgerMenu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();

  const navItems = [
    { name: 'Home', path: '/' },
    { name: 'Projects', path: '/projects' },
    { name: 'Gallery', path: '/gallery' },
    { name: 'Contact', path: '/contact' },
  ];

  const isActive = (path) => location.pathname === path;

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleMenuClick = () => {
    setIsMenuOpen(false);
  };

  // Close menu when route changes
  useEffect(() => {
    setIsMenuOpen(false);
  }, [location.pathname]);

  return (
    <div className="hamburger-menu-container">
      
      <input
        type="checkbox"
        className="hamburger-checkbox"
        checked={isMenuOpen}
        onChange={handleMenuToggle}
        tabIndex="-1"
        aria-hidden="true"
      />

      {/* Hamburger button */}
      <label
        htmlFor="hamburger-checkbox"
        className="hamburger-label"
        tabIndex="0"
        role="button"
        aria-label="Toggle navigation menu"
        onKeyDown={(e) => {
          if (e.key === 'Enter' || e.key === ' ') {
            e.preventDefault();
            handleMenuToggle();
          }
        }}
      >
        <div className="hamburger-icon">
          <span></span>
          <span></span>
          <span></span>
        </div>
      </label>

      {/* Menu overlay */}
      <div className={`menu-overlay ${isMenuOpen ? 'menu-open' : ''}`}>
        <div className="menu-content">
          {navItems.map((item, index) => (
            <Link
              key={item.name}
              to={item.path}
              className={`menu-item ${isActive(item.path) ? 'active' : ''}`}
              onClick={handleMenuClick}
              tabIndex="0"
              style={{ '--item-color': `hsl(${index * 90}, 70%, 60%)` }}
            >
              {item.name}
            </Link>
          ))}
          
          {/* Animated background blob */}
          <div className="move-item"></div>
        </div>
      </div>
    </div>
  );
};

export default AnimatedHamburgerMenu;
