import React, { useEffect, useState, useRef } from 'react';
import { motion } from 'framer-motion';
import { FaGithub, FaLinkedin, FaTwitter, FaEnvelope, FaHeart } from 'react-icons/fa';
import ProjectCard from '../components/ProjectCard';
import axios from 'axios';

// Typewriter component
const Typewriter = ({ text, speed = 75, className = "" }) => {
  const [displayText, setDisplayText] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    if (currentIndex < text.length) {
      const timeout = setTimeout(() => {
        setDisplayText(prev => prev + text[currentIndex]);
        setCurrentIndex(prev => prev + 1);
      }, speed);
      return () => clearTimeout(timeout);
    }
  }, [currentIndex, text, speed]);

  return (
    <span className={className}>
      {displayText}
      <span className="animate-pulse">|</span>
    </span>
  );
};

// Magnetic repulsion for image wrapper
const MagneticImageWrapper = ({ imageUrl, children }) => {
  const containerRef = useRef(null);
  const [movement, setMovement] = useState({ x: 0, y: 0 });

  const handleMouseMove = (e) => {
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const moveX = Math.max(Math.min((centerX - x) * 0.15, 30), -30);
    const moveY = Math.max(Math.min((centerY - y) * 0.15, 30), -30);
    setMovement({ x: moveX, y: moveY });
  };

  const handleMouseLeave = () => {
    setMovement({ x: 0, y: 0 });
  };

  return (
    <div
      ref={containerRef}
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
      className="relative group overflow-hidden rounded-lg h-96 bg-black shadow-2xl transition-transform duration-300"
      style={{ transform: `translate(${movement.x}px, ${movement.y}px)` }}
    >
      <div
        className="absolute inset-0 bg-cover bg-center bg-no-repeat transition-all duration-500 scale-105 group-hover:scale-110"
        style={{ backgroundImage: `url('${imageUrl}')` }}
      ></div>
      <div className="absolute inset-0 bg-black bg-opacity-50 transition-all duration-300 group-hover:bg-opacity-30"></div>
      {children && (
        <div className="absolute inset-0 flex items-center justify-center">
          {children}
        </div>
      )}
      <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-0 group-hover:opacity-20 transition-opacity duration-500 transform -skew-x-12"></div>
    </div>
  );
};

// Optional magnetic text if you want to keep it inside image
const MagneticText = ({ text, className = "" }) => {
  const containerRef = useRef(null);
  const [movement, setMovement] = useState({ x: 0, y: 0, opacity: 1 });

  const handleMouseMove = (e) => {
    const rect = containerRef.current.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const moveX = Math.max(Math.min((centerX - x) * 0.35, 60), -60);
    const moveY = Math.max(Math.min((centerY - y) * 0.35, 60), -60);
    const distance = Math.sqrt(Math.pow(centerX - x, 2) + Math.pow(centerY - y, 2));
    const opacity = distance < 30 ? 0 : 1;
    setMovement({ x: moveX, y: moveY, opacity });
  };

  const handleMouseLeave = () => {
    setMovement({ x: 0, y: 0, opacity: 1 });
  };

  return (
    <div
      ref={containerRef}
      className={`relative w-full h-full flex items-center justify-center ${className}`}
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
      style={{ minHeight: 200 }}
    >
      <h1
        className="text-6xl font-bold text-white transition-all duration-300 ease-out select-none"
        style={{
          transform: `translate(${movement.x}px, ${movement.y}px)`,
          opacity: movement.opacity,
        }}
      >
        {text}
      </h1>
    </div>
  );
};

