import React, { useState } from 'react';
import './AnimatedHamburgerMenu.css';

const AnimatedHamburgerMenu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const menuItems = [
    { name: 'Home', color: '#DEF06C' },
    { name: 'Projects', color: '#71C1D1' },
    { name: 'Gallery', color: '#DD7171' },
    { name: 'Contact', color: '#A871D1' }
  ];

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleMenuItemClick = () => {
    setIsMenuOpen(false);
  };

  return (
    <div className="hamburger-menu-container">
      {/* Hidden checkbox for CSS animations */}
      <input
        type="checkbox"
        id="hamburger-toggle"
        className="hamburger-checkbox"
        checked={isMenuOpen}
        onChange={handleMenuToggle}
      />
      
      {/* Hamburger icon */}
      <label htmlFor="hamburger-toggle" className="hamburger-label">
        <div className="hamburger-icon">
          <span></span>
          <span></span>
          <span></span>
        </div>
      </label>

      {/* Sliding menu */}
      <div className={`menu-overlay ${isMenuOpen ? 'menu-open' : ''}`}>
        <div className="menu-content">
          {menuItems.map((item, index) => (
            <div
              key={item.name}
              className="menu-item"
              style={{ '--item-color': item.color }}
              onClick={handleMenuItemClick}
            >
              {item.name}
            </div>
          ))}
          <div className="move-item"></div>
        </div>
      </div>
    </div>
  );
};

export default AnimatedHamburgerMenu;
